import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './ProviderDashboard.css';

const ProviderDashboard = ({ user }) => {
    const [listings, setListings] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchMyListings = async () => {
            try {
                const token = localStorage.getItem('authToken');
                const res = await fetch('http://localhost:5000/api/listings/my-listings', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch your listings');
                }
                const data = await res.json();
                setListings(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMyListings();
    }, []);

    const handleDelete = async (listingId) => {
        if (!window.confirm("Are you sure you want to delete this listing?")) return;

        try {
            const token = localStorage.getItem('authToken');
            const res = await fetch(`http://localhost:5000/api/listings/${listingId}`, {
                method: 'DELETE',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!res.ok) {
                throw new Error('Failed to delete listing');
            }
            // Refresh listings by filtering out the deleted one
            setListings(listings.filter(l => l.id !== listingId));
        } catch (err) {
            setError(err.message);
        }
    };


    if (loading) return <div>Loading your listings...</div>;

    return (
        <div className="provider-dashboard">
            <header className="dashboard-header">
                <h1>Provider Dashboard</h1>
                <p>Manage your service listings below.</p>
                <Link to="/create-listing" className="btn-create-listing">
                    + Add New Service
                </Link>
            </header>

            {error && <div className="error-message">{error}</div>}

            <div className="my-listings-container">
                <h2>Your Services</h2>
                {listings.length > 0 ? (
                    <table className="listings-table">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Category</th>
                                <th>Price</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {listings.map(listing => (
                                <tr key={listing.id}>
                                    <td>{listing.title}</td>
                                    <td>{listing.category_name}</td>
                                    <td>${listing.price}</td>
                                    <td>
                                        <button className="btn-action btn-edit">Edit</button>
                                        <button className="btn-action btn-delete" onClick={() => handleDelete(listing.id)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>You haven't created any service listings yet. Click "Add New Service" to get started!</p>
                )}
            </div>
        </div>
    );
};

export default ProviderDashboard;
