import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import './App.css'; // Import the stylesheet

/**
 * A simple Home component to act as the application's landing page.
 */
const Home = () => {
  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center', 
      justifyContent: 'center', 
      height: '100vh',
      fontFamily: 'sans-serif',
      textAlign: 'center',
      padding: '2rem'
    }}>
      <h1 style={{ color: '#333', marginBottom: '1rem' }}>Local Service Finder</h1>
      <p style={{ color: '#666', maxWidth: '400px', marginBottom: '2rem' }}>
        The easiest way to find and book trusted local professionals for any service you need.
      </p>
      <div style={{ display: 'flex', gap: '1rem' }}>
          <Link to="/login">
            <button style={{
              padding: '12px 24px',
              fontSize: '1rem',
              color: '#4f46e5',
              backgroundColor: 'white',
              border: '2px solid #fcd810ff',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}>
              Login
            </button>
          </Link>
          <Link to="/signup">
            <button style={{
              padding: '12px 24px',
              fontSize: '1rem',
              color: 'white',
              backgroundColor: '#65da06ff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: 'bold',
            }}>
              Sign Up
            </button>
          </Link>
      </div>
    </div>
  );
};


function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
