import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './BookingPage.css'; // New styles for the "wow" UI

const BookingPage = () => {
    const { listingId } = useParams();
    const [listing, setListing] = useState(null);
    const [slots, setSlots] = useState([]);
    const [selectedSlot, setSelectedSlot] = useState(null);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchListingDetails = async () => {
            try {
                const res = await fetch(`http://localhost:5000/api/listings/${listingId}`);
                if (!res.ok) throw new Error('Could not find this service listing.');
                const data = await res.json();
                setListing(data);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchListingDetails();
    }, [listingId]);

    useEffect(() => {
        if (listing && listing.provider_id) {
            const fetchSlots = async () => {
                try {
                    const res = await fetch(`http://localhost:5000/api/bookings/slots/${listing.provider_id}`);
                    if (!res.ok) throw new Error('Failed to fetch available slots for this provider.');
                    const data = await res.json();
                    setSlots(data);
                } catch (err) {
                    setError(err.message);
                } finally {
                    setLoading(false);
                }
            };
            fetchSlots();
        }
    }, [listing]);

    const handleBooking = async () => {
        if (!selectedSlot) return setError("Please select a time slot to continue.");
        
        const token = localStorage.getItem('authToken');
        if (!token) return navigate('/login');

        try {
            const res = await fetch('http://localhost:5000/api/bookings/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    provider_id: listing.provider_id,
                    listing_id: parseInt(listingId, 10), // Ensure listingId is a number
                    booking_time: selectedSlot
                })
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.message || 'Failed to create booking.');
            }
            
            alert('Booking successful! The provider has been notified.');
            navigate('/dashboard');
        // FIXED: The fat arrow "=>" has been removed from the catch block.
        } catch (err) { 
            setError(err.message);
        }
    };
    
    // Group slots by date for a better UI
    const groupedSlots = useMemo(() => {
        return slots.reduce((acc, slot) => {
            const date = new Date(slot).toDateString();
            if (!acc[date]) {
                acc[date] = [];
            }
            acc[date].push(slot);
            return acc;
        }, {});
    }, [slots]);

    const formatDateHeader = (dateString) => new Date(dateString).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' });
    const formatTime = (dateString) => new Date(dateString).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', hour12: true });

    if (loading) return <div className="loading-message">Loading booking calendar...</div>;

    return (
        <div className="booking-page-container">
            <div className="booking-card">
                {listing && (
                    <div className="booking-header">
                        <h2>Book: {listing.title}</h2>
                        <p>with {listing.provider_name}</p>
                    </div>
                )}
                
                <div className="slots-container">
                    {Object.keys(groupedSlots).length > 0 ? (
                        Object.entries(groupedSlots).map(([date, timeSlots]) => (
                            <div key={date} className="date-group">
                                <h3>{formatDateHeader(date)}</h3>
                                <div className="time-slots-grid">
                                    {timeSlots.map(slot => (
                                        <button 
                                            key={slot} 
                                            className={`slot-btn ${selectedSlot === slot ? 'selected' : ''}`}
                                            onClick={() => setSelectedSlot(slot)}
                                        >
                                            {formatTime(slot)}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="no-slots-message">No available slots in the next 7 days for this provider.</p>
                    )}
                </div>
                
                {error && <p className="error-message">{error}</p>}
                
                <button className="form-button" onClick={handleBooking} disabled={!selectedSlot}>
                    Confirm Booking
                </button>
            </div>
        </div>
    );
};

export default BookingPage;
