import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [providers, setProviders] = useState([]);
    const [analytics, setAnalytics] = useState(null); // NEW: State for analytics data
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    const fetchData = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('authToken');
            if (!token) return navigate('/login');

            // Fetch both providers and analytics data concurrently
            const [providersRes, analyticsRes] = await Promise.all([
                fetch('http://localhost:5000/api/admin/providers', { headers: { 'Authorization': `Bearer ${token}` } }),
                fetch('http://localhost:5000/api/admin/analytics', { headers: { 'Authorization': `Bearer ${token}` } })
            ]);

            if (providersRes.status === 403 || analyticsRes.status === 403) throw new Error('Access Denied. You are not an administrator.');
            if (!providersRes.ok) throw new Error('Failed to load provider data.');
            if (!analyticsRes.ok) throw new Error('Failed to load analytics data.');
            
            const providersData = await providersRes.json();
            const analyticsData = await analyticsRes.json();
            
            setProviders(providersData);
            setAnalytics(analyticsData); // NEW: Set analytics state
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, [navigate]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleStatusChange = async (providerId, newStatus) => {
        // ... (existing code for status change)
        try {
            const token = localStorage.getItem('authToken');
            const res = await fetch(`http://localhost:5000/api/admin/providers/${providerId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status: newStatus })
            });

            if (!res.ok) throw new Error('Failed to update status.');
            fetchData(); // Refresh all data
        } catch (err) {
            setError(err.message);
        }
    };
    
    const formatDate = (dateString) => new Date(dateString).toLocaleDateString();

    if (loading) return <div className="loading-message">Loading Admin Panel...</div>;

    return (
        <div className="admin-dashboard-container">
            <div className="admin-dashboard-card">
                <header className="admin-header">
                    <h1>Admin Dashboard</h1>
                    <p>Platform Overview & Moderation</p>
                </header>
                
                {error && <div className="error-message">{error}</div>}

                {/* --- NEW: Analytics Section --- */}
                {analytics && (
                    <div className="analytics-grid">
                        <div className="stat-card">
                            <h3>Total Users</h3>
                            <p>{analytics.totalUsers}</p>
                        </div>
                         <div className="stat-card">
                            <h3>Approved Providers</h3>
                            <p>{analytics.totalProviders}</p>
                        </div>
                         <div className="stat-card">
                            <h3>Total Bookings</h3>
                            <p>{analytics.totalBookings}</p>
                        </div>
                        <div className="stat-card top-categories">
                            <h3>Top Categories</h3>
                            <ul>
                                {analytics.topCategories.map(cat => (
                                    <li key={cat.name}>
                                        <span>{cat.name}</span>
                                        <strong>{cat.bookingCount}</strong>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                )}

                {/* --- Provider Moderation Section --- */}
                <div className="moderation-section">
                    <h2>Provider Moderation</h2>
                    <table className="admin-table">
                        {/* ... (existing table code) ... */}
                        <thead>
                        <tr>
                            <th>Provider Name</th>
                            <th>Email</th>
                            <th>Location</th>
                            <th>Registered On</th>
                            <th>Status</th>
                            <th style={{ textAlign: 'center' }}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {providers.map(provider => (
                            <tr key={provider.id}>
                                <td>{provider.name}</td>
                                <td>{provider.email}</td>
                                <td>{provider.location}</td>
                                <td>{formatDate(provider.created_at)}</td>
                                <td>
                                    <span className={`status-badge status-${provider.status.toLowerCase()}`}>
                                        {provider.status}
                                    </span>
                                </td>
                                <td className="action-buttons">
                                    {provider.status === 'Pending' && (
                                        <button className="btn-action btn-approve" onClick={() => handleStatusChange(provider.id, 'Approved')}>Approve</button>
                                    )}
                                    {provider.status === 'Approved' && (
                                        <button className="btn-action btn-suspend" onClick={() => handleStatusChange(provider.id, 'Suspended')}>Suspend</button>
                                    )}
                                     {provider.status === 'Suspended' && (
                                        <button className="btn-action btn-approve" onClick={() => handleStatusChange(provider.id, 'Approved')}>Re-Approve</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
